    result.localStorageAvailable = true;
  } catch {
    // localStorage ä¸å¯ç”¨
  }

  // æ£€æŸ¥ sessionStorage
  try {
    const testKey = '__test_sessionStorage__';
    sessionStorage.setItem(testKey, 'test');
    sessionStorage.removeItem(testKey);
    result.sessionStorageAvailable = true;
  } catch {
    // sessionStorage ä¸å¯ç”¨
  }

  // æ£€æŸ¥ Cookies
  try {
