          message: `ä¿®å¤åŒæ­¥é—®é¢˜å¤±è´¥: ${r.error || 'æœªçŸ¥é”™è¯¯'}`,
        };
  }

  private static runCompact() {
    const r = this.compactStorage();
    return r.success
      ? { success: true, message: 'å‹ç¼©å­˜å‚¨æˆåŠŸ' }
      : { success: false, message: `å‹ç¼©å­˜å‚¨å¤±è´¥: ${r.error || 'æœªçŸ¥é”™è¯¯'}` };
  }

  /**
   * å‹ç¼©å­˜å‚¨ç©ºé—´
   * Compact storage space
   */
  static compactStorage(): StorageOperationResult {
    try {
      let compactedItems = ZERO;

      // é‡æ–°åºåˆ—åŒ–æ‰€æœ‰æ•°æ®ä»¥ç§»é™¤å¤šä½™ç©ºæ ¼
      Object.values(STORAGE_KEYS).forEach((key) => {
        const data = LocalStorageManager.get(key);
        if (data !== null) {
          // é‡æ–°è®¾ç½®æ•°æ®ï¼Œè¿™ä¼šè§¦å‘é‡æ–°åºåˆ—åŒ–
          LocalStorageManager.set(key, data);
          compactedItems += ONE;
        }
      });

      return {
        success: true,
        error: `å·²å‹ç¼© ${compactedItems} é¡¹å­˜å‚¨æ•°æ®`,
        timestamp: Date.now(),
        data: { compactedItems },
      };
    } catch (error) {
      return {
        success: false,
        error: `å‹ç¼©å­˜å‚¨å¤±è´¥: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * ä¼˜åŒ–æ£€æµ‹å†å²æ•°æ®
   * Optimize detection history data
   */
  static optimizeDetectionHistory(): StorageOperationResult {
    try {
      const historyData = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );

      if (!historyData || !Array.isArray(historyData.detections)) {
        return {
          success: true,
          error: 'æ²¡æœ‰æ£€æµ‹å†å²æ•°æ®éœ€è¦ä¼˜åŒ–',
          timestamp: Date.now(),
        };
      }

      const originalCount = historyData.detections.length;

      // æŒ‰æ—¶é—´æ’åºï¼ˆæœ€æ–°çš„åœ¨å‰ï¼‰
      const sortedDetections = historyData.detections.sort(
        (a, b) => b.timestamp - a.timestamp,
      );

      // ä¿ç•™æœ€è¿‘çš„100æ¡è®°å½•
      const maxRecords = PERCENTAGE_FULL;
      const optimizedDetections = sortedDetections.slice(ZERO, maxRecords);

      const removedCount = originalCount - optimizedDetections.length;

      if (removedCount > ZERO) {
        const updatedHistory: LocaleDetectionHistory = {
          detections: optimizedDetections,
          history: optimizedDetections,
          lastUpdated: Date.now(),
          totalDetections: optimizedDetections.length,
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
          updatedHistory,
        );

        return {
          success: true,
          error: `å·²ä¼˜åŒ–æ£€æµ‹å†å²ï¼Œç§»é™¤ ${removedCount} æ¡æ—§è®°å½•`,
          timestamp: Date.now(),
          data: { removedCount, remainingCount: optimizedDetections.length },
        };
      }

      return {
        success: true,
        error: 'æ£€æµ‹å†å²æ•°æ®å·²æ˜¯æœ€ä¼˜çŠ¶æ€',
        timestamp: Date.now(),
        data: { removedCount: ZERO, remainingCount: originalCount },
      };
    } catch (error) {
      return {
        success: false,
        error: `ä¼˜åŒ–æ£€æµ‹å†å²å¤±è´¥: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * é‡å»ºå­˜å‚¨ç´¢å¼•
   * Rebuild storage index
   */
  static rebuildStorageIndex(): StorageOperationResult {
    try {
      let rebuiltItems = ZERO;
      const actions: string[] = [];

      // é‡å»ºåå¥½æ•°æ®ç´¢å¼•
      const preference = LocalStorageManager.get<UserLocalePreference>(
        STORAGE_KEYS.LOCALE_PREFERENCE,
      );
      if (preference) {
        // ç¡®ä¿æ•°æ®ç»“æ„å®Œæ•´
        const rebuiltPreference: UserLocalePreference = {
          locale: preference.locale,
          source: preference.source,
          timestamp: preference.timestamp,
          confidence: preference.confidence,
          ...(preference.metadata && { metadata: preference.metadata }),
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_PREFERENCE,
          rebuiltPreference,
        );
        rebuiltItems += ONE;
        actions.push('é‡å»ºåå¥½æ•°æ®ç´¢å¼•');
      }

      // é‡å»ºå†å²æ•°æ®ç´¢å¼•
      const history = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );
      if (history) {
        // ç¡®ä¿æ•°æ®ç»“æ„å®Œæ•´å¹¶æŒ‰æ—¶é—´æ’åº
        const filteredDetections = history.detections
          .filter(
            (detection) => detection && detection.locale && detection.source,
          )
          .sort((a, b) => b.timestamp - a.timestamp);

        const rebuiltHistory: LocaleDetectionHistory = {
          detections: filteredDetections,
          history: filteredDetections,
          lastUpdated: Date.now(),
          totalDetections: filteredDetections.length,
        };

        LocalStorageManager.set(
          STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
          rebuiltHistory,
        );
        rebuiltItems += ONE;
        actions.push('é‡å»ºå†å²æ•°æ®ç´¢å¼•');
      }

      return {
        success: true,
        error: `å·²é‡å»º ${rebuiltItems} é¡¹å­˜å‚¨ç´¢å¼•`,
        timestamp: Date.now(),
        data: { rebuiltItems, actions },
      };
    } catch (error) {
      return {
        success: false,
        error: `é‡å»ºå­˜å‚¨ç´¢å¼•å¤±è´¥: ${error instanceof Error ? error.message : 'æœªçŸ¥é”™è¯¯'}`,
        timestamp: Date.now(),
      };
    }
  }

  /**
   * æ‰§è¡Œæ·±åº¦ç»´æŠ¤
   * Perform deep maintenance
   */
  static performDeepMaintenance(): StorageOperationResult {
    try {
      const results: string[] = [];
      let total = ZERO;
      let ok = ZERO;

      const tasks: Array<{ name: string; run: () => StorageOperationResult }> =
        [
          {
            name: 'æ ‡å‡†ç»´æŠ¤',
            run: () =>
              this.performMaintenance({
                cleanupExpired: true,
                validateData: true,
                compactStorage: true,
                fixSyncIssues: true,
                cleanupDuplicates: true,
                cleanupInvalid: true,
              }),
          },
          { name: 'ä¼˜åŒ–å†å²', run: () => this.optimizeDetectionHistory() },
          { name: 'é‡å»ºç´¢å¼•', run: () => this.rebuildStorageIndex() },
        ];

      for (const t of tasks) {
        total += ONE;
        const r = t.run();
        if (r.success) {
          ok += ONE;
          results.push(`${t.name}å®Œæˆ`);
        } else {
          results.push(`${t.name}å¤±è´¥: ${r.error || 'æœªçŸ¥é”™è¯¯'}`);
        }
      }

      return {
        success: ok === total,
        error: `æ·±åº¦ç»´æŠ¤å®Œæˆ: ${ok}/${total} æ“ä½œæˆåŠŸ`,
        timestamp: Date.now(),
        data: { totalOperations: total, successfulOperations: ok, results },
      };
    } catch {
      return {
        success: false,
        error: 'æ‰§è¡Œæ·±åº¦ç»´æŠ¤å¤±è´¥: æœªçŸ¥é”™è¯¯',
        timestamp: Date.now(),
      };
    }
  }

  /**
   * è·å–ç»´æŠ¤å»ºè®®
   * Get maintenance recommendations
   */
  static getMaintenanceRecommendations(): {
    recommendations: string[];
    priority: 'low' | 'medium' | 'high';
    estimatedTime: string;
  } {
    const recommendations: string[] = [];
    const levelsEncountered = new Set<'low' | 'medium' | 'high'>();

    const add = (msg: string, level: 'low' | 'medium' | 'high') => {
      recommendations.push(msg);
      levelsEncountered.add(level);
    };

    try {
      const cleanup = LocaleCleanupManager.getCleanupStats();
      if (cleanup.expiredDetections > PERCENTAGE_HALF)
        add(`æ¸…ç† ${cleanup.expiredDetections} æ¡è¿‡æœŸæ£€æµ‹è®°å½•`, 'medium');
      if (cleanup.duplicateDetections > COUNT_TEN)
        add(`æ¸…ç† ${cleanup.duplicateDetections} æ¡é‡å¤æ£€æµ‹è®°å½•`, 'medium');
      if (cleanup.invalidPreferences > ZERO)
        add(`ä¿®å¤ ${cleanup.invalidPreferences} é¡¹æ— æ•ˆåå¥½æ•°æ®`, 'high');

      const summary = LocaleValidationManager.getValidationSummary();
      if (summary.invalidKeys > ZERO)
        add(`ä¿®å¤ ${summary.invalidKeys} é¡¹æ— æ•ˆæ•°æ®`, 'high');
      if (summary.syncIssues > ZERO)
        add(`ä¿®å¤ ${summary.syncIssues} ä¸ªåŒæ­¥é—®é¢˜`, 'medium');

      const history = LocalStorageManager.get<LocaleDetectionHistory>(
        STORAGE_KEYS.LOCALE_DETECTION_HISTORY,
      );
      if (history?.detections && history.detections.length > HTTP_OK) {
        const historyLevel: 'medium' | 'high' = levelsEncountered.has('high')
          ? 'high'
          : 'medium';
        add('ä¼˜åŒ–æ£€æµ‹å†å²æ•°æ®ï¼ˆè®°å½•è¿‡å¤šï¼‰', historyLevel);
      }

      if (recommendations.length === ZERO) {
        add('å­˜å‚¨çŠ¶æ€è‰¯å¥½ï¼Œæ— éœ€ç‰¹æ®Šç»´æŠ¤', 'low');
      }

      const priority: 'low' | 'medium' | 'high' = levelsEncountered.has('high')
        ? 'high'
        : levelsEncountered.has('medium')
          ? 'medium'
          : 'low';

      let estimatedTime: string;
      switch (priority) {
        case 'high':
          estimatedTime = '2-5åˆ†é’Ÿ';
          break;
        case 'medium':
          estimatedTime =
            recommendations.length > COUNT_TRIPLE ? '1-2åˆ†é’Ÿ' : '< 1åˆ†é’Ÿ';
          break;
        case 'low':
        default:
          estimatedTime =
            recommendations.length > COUNT_TRIPLE ? '1-2åˆ†é’Ÿ' : '< 1åˆ†é’Ÿ';
          break;
      }

      return { recommendations, priority, estimatedTime };
    } catch {
      return {
        recommendations: ['è·å–ç»´æŠ¤å»ºè®®æ—¶å‡ºé”™ï¼Œå»ºè®®æ‰§è¡ŒåŸºç¡€ç»´æŠ¤'],
        priority: 'medium',
        estimatedTime: '1-2åˆ†é’Ÿ',
      };
    }
  }
}

