  /**
   * è·å–ç‰¹å®šæ¨¡å—çš„é…ç½®
   * Get specific module configuration
   */
  getModuleConfig<T extends keyof PerformanceConfig>(
    module: T,
  ): PerformanceConfig[T] | undefined {
    // ä¼˜å…ˆåŒ¹é…ä¸»è¦æ¨¡å—ï¼Œé™ä½åˆ†æ”¯å¤æ‚åº¦
    const primary = this.getPrimaryModuleConfig(module);
    if (primary !== undefined) return primary as PerformanceConfig[T];

    switch (module) {
      case 'webVitals':
        return this.config.webVitals
          ? ({ ...this.config.webVitals } as PerformanceConfig[T])
          : undefined;
      case 'component':
        return this.config.component
          ? ({ ...this.config.component } as PerformanceConfig[T])
          : undefined;
      case 'network':
        return this.config.network
          ? ({ ...this.config.network } as PerformanceConfig[T])
          : undefined;
      case 'bundle':
        return this.config.bundle
          ? ({ ...this.config.bundle } as PerformanceConfig[T])
          : undefined;
      case 'debug':
        return this.config.debug as PerformanceConfig[T];
      case 'global':
        return this.config.global
          ? ({ ...this.config.global } as PerformanceConfig[T])
          : undefined;
      default:
        // å…œåº•ï¼šè¿”å› undefinedï¼ˆä¸åº”è§¦è¾¾ï¼‰
        return undefined;
    }
  }

  private getPrimaryModuleConfig<T extends keyof PerformanceConfig>(
    module: T,
  ): PerformanceConfig[T] | undefined {
    switch (module) {
      case 'reactScan':
        return { ...this.config.reactScan } as PerformanceConfig[T];
      case 'webEvalAgent':
        return { ...this.config.webEvalAgent } as PerformanceConfig[T];
      case 'bundleAnalyzer':
        return { ...this.config.bundleAnalyzer } as PerformanceConfig[T];
      case 'sizeLimit':
        return { ...this.config.sizeLimit } as PerformanceConfig[T];
      default:
        return undefined;
    }
  }

  /**
   * æ›´æ–°ç‰¹å®šæ¨¡å—çš„é…ç½®
   * Update specific module configuration
   */
  updateModuleConfig<T extends keyof PerformanceConfig>(
    module: T,
    config: Partial<PerformanceConfig[T]>,
  ): void {
    const current = this.getModuleConfig(module);
    const isMergeableObject = (
      value: unknown,
    ): value is Record<string, unknown> =>
      typeof value === 'object' && value !== null && !Array.isArray(value);

    let nextValue: PerformanceConfig[T] | undefined;

    if (isMergeableObject(current) && isMergeableObject(config)) {
      nextValue = {
        ...(current as Record<string, unknown>),
        ...(config as Record<string, unknown>),
      } as PerformanceConfig[T];
    } else if (config !== undefined) {
      nextValue = config as PerformanceConfig[T];
    } else {
      nextValue = current;
    }

    switch (module) {
      case 'reactScan':
        this.config.reactScan = (nextValue ??
          this.config.reactScan) as PerformanceConfig['reactScan'];
        break;
      case 'webEvalAgent':
        this.config.webEvalAgent = (nextValue ??
          this.config.webEvalAgent) as PerformanceConfig['webEvalAgent'];
        break;
      case 'bundleAnalyzer':
        this.config.bundleAnalyzer = (nextValue ??
          this.config.bundleAnalyzer) as PerformanceConfig['bundleAnalyzer'];
        break;
      case 'sizeLimit':
        this.config.sizeLimit = (nextValue ??
          this.config.sizeLimit) as PerformanceConfig['sizeLimit'];
        break;
      case 'webVitals':
        if (nextValue !== undefined) {
          this.config.webVitals = nextValue as NonNullable<
            PerformanceConfig['webVitals']
          >;
        } else {
          delete this.config.webVitals;
        }
        break;
      case 'component':
        if (nextValue !== undefined) {
          this.config.component = nextValue as NonNullable<
            PerformanceConfig['component']
          >;
        } else {
          delete this.config.component;
        }
        break;
      case 'network':
        if (nextValue !== undefined) {
          this.config.network = nextValue as NonNullable<
            PerformanceConfig['network']
          >;
        } else {
          delete this.config.network;
        }
        break;
      case 'bundle':
        if (nextValue !== undefined) {
          this.config.bundle = nextValue as NonNullable<
            PerformanceConfig['bundle']
          >;
        } else {
          delete this.config.bundle;
        }
        break;
      case 'debug':
        if (typeof nextValue === 'boolean') {
          this.config.debug = nextValue;
        } else if (nextValue === undefined) {
          delete this.config.debug;
        }
        break;
      case 'global':
        if (nextValue !== undefined) {
          this.config.global = nextValue as NonNullable<
            PerformanceConfig['global']
          >;
        } else {
          delete this.config.global;
        }
        break;
      default:
        break;
    }

    // éªŒè¯æ›´æ–°åçš„é…ç½®
    const validation = validateConfig(this.config);
    if (!validation.isValid) {
      logger.warn(`Updated ${module} config validation failed`, {
        errors: validation.errors,
      });
    }
  }

  /**
   * å¯¼å‡ºé…ç½®ä¸ºJSON
   * Export configuration as JSON
   */
  exportConfig(): string {
    return JSON.stringify(this.config, null, COUNT_PAIR);
  }

  /**
   * ä»JSONå¯¼å…¥é…ç½®
   * Import configuration from JSON
   */
  importConfig(configJson: string): boolean {
    try {
      const importedConfig = JSON.parse(configJson) as PerformanceConfig;

      // éªŒè¯å¯¼å…¥çš„é…ç½®
      const validation = validateConfig(importedConfig);
      if (!validation.isValid) {
        logger.error('Imported config validation failed', {
          errors: validation.errors,
        });
        return false;
      }

      this.config = importedConfig;
      return true;
    } catch (error) {
      logger.error('Failed to import config', { error: error as Error });
      return false;
    }
  }

  /**
   * è·å–é…ç½®æ‘˜è¦
   * Get configuration summary
   */
  getConfigSummary(): {
    isEnabled: boolean;
    enabledModules: string[];
    dataRetentionTime: number;
    maxMetrics: number;
    thresholds: Record<string, number>;
  } {
    const { global, component, network, bundle } = this.config;

    const enabledModules = this.getEnabledModules(
      component?.enabled,
      network?.enabled,
      bundle?.enabled,
    );

    const isEnabled = Boolean(global?.enabled);
    const dataRetentionTime =
      global?.dataRetentionTime ?? PERFORMANCE_CONSTANTS.DEFAULT_RETENTION_TIME;
    const maxMetrics =
      global?.maxMetrics ?? PERFORMANCE_CONSTANTS.DEFAULT_MAX_METRICS;

    return {
      isEnabled,
      enabledModules,
      dataRetentionTime,
      maxMetrics,
      thresholds: this.buildThresholds(
        component?.thresholds?.renderTime,
        network?.thresholds?.responseTime,
        bundle?.thresholds?.size,
      ),
    };
  }

  private getEnabledModules(
    componentEnabled?: boolean,
    networkEnabled?: boolean,
    bundleEnabled?: boolean,
  ): string[] {
    const list: string[] = [];
    if (componentEnabled) list.push('component');
    if (networkEnabled) list.push('network');
    if (bundleEnabled) list.push('bundle');
    return list;
  }

  private buildThresholds(
    componentRenderTime?: number,
    networkResponseTime?: number,
    bundleSize?: number,
  ): Record<string, number> {
    return {
      componentRenderTime: componentRenderTime || PERCENTAGE_FULL,
      networkResponseTime: networkResponseTime || ANIMATION_DURATION_NORMAL,
      bundleSize: bundleSize || MB,
    };
  }

  /**
   * æ¯”è¾ƒä¸¤ä¸ªé…ç½®
   * Compare two configurations
   */
  compareConfigs(otherConfig: PerformanceConfig): {
    isDifferent: boolean;
    differences: string[];
  } {
    const differences: string[] = [];
    const current = this.config;

    this.compareGlobalConfig(current, otherConfig, differences);
    this.compareComponentConfig(current, otherConfig, differences);
    this.compareNetworkConfig(current, otherConfig, differences);
    this.compareBundleConfig(current, otherConfig, differences);

    return { isDifferent: differences.length > ZERO, differences };
  }

  private compareGlobalConfig(
    current: PerformanceConfig,
    otherConfig: PerformanceConfig,
    differences: string[],
  ): void {
    if (current.global?.enabled !== otherConfig.global?.enabled) {
      differences.push('global.enabled');
    }
    if (
      current.global?.dataRetentionTime !==
      otherConfig.global?.dataRetentionTime
    ) {
      differences.push('global.dataRetentionTime');
    }
    if (current.global?.maxMetrics !== otherConfig.global?.maxMetrics) {
      differences.push('global.maxMetrics');
    }
  }

  private compareComponentConfig(
    current: PerformanceConfig,
    otherConfig: PerformanceConfig,
    differences: string[],
  ): void {
    if (current.component?.enabled !== otherConfig.component?.enabled) {
      differences.push('component.enabled');
    }
    if (
      current.component?.thresholds?.renderTime !==
      otherConfig.component?.thresholds?.renderTime
    ) {
      differences.push('component.thresholds.renderTime');
    }
  }

  private compareNetworkConfig(
    current: PerformanceConfig,
    otherConfig: PerformanceConfig,
    differences: string[],
  ): void {
    if (current.network?.enabled !== otherConfig.network?.enabled) {
      differences.push('network.enabled');
    }
    if (
      current.network?.thresholds?.responseTime !==
      otherConfig.network?.thresholds?.responseTime
    ) {
      differences.push('network.thresholds.responseTime');
    }
  }

  private compareBundleConfig(
    current: PerformanceConfig,
    otherConfig: PerformanceConfig,
    differences: string[],
  ): void {
    if (current.bundle?.enabled !== otherConfig.bundle?.enabled) {
      differences.push('bundle.enabled');
    }
    if (
      current.bundle?.thresholds?.size !== otherConfig.bundle?.thresholds?.size
    ) {
      differences.push('bundle.thresholds.size');
    }
  }

  /**
   * è·å–é…ç½®å˜æ›´å†å²
   * Get configuration change history
   */
  private configHistory: Array<{
    timestamp: number;
    config: PerformanceConfig;
    reason?: string;
  }> = [];

  /**
   * è®°å½•é…ç½®å˜æ›´
   * Record configuration change
   */
  private recordConfigChange(reason?: string): void {
    this.configHistory.push({
      timestamp: Date.now(),
      config: { ...this.config },
      ...(reason !== undefined && { reason }),
    });

    // é™åˆ¶å†å²è®°å½•æ•°é‡
    if (this.configHistory.length > COUNT_TEN) {
      this.configHistory = this.configHistory.slice(-COUNT_TEN);
    }
  }

  /**
   * è·å–é…ç½®å˜æ›´å†å²
   * Get configuration change history
   */
  getConfigHistory(): typeof this.configHistory {
    return [...this.configHistory];
  }

  /**
   * å›æ»šåˆ°ä¹‹å‰çš„é…ç½®
   * Rollback to previous configuration
   */
  rollbackConfig(steps = ONE): boolean {
    if (this.configHistory.length < steps + ONE) {
      return false;
    }

    const targetConfig =
      this.configHistory[this.configHistory.length - steps - ONE];
    if (targetConfig?.config) {
      // ç›´æ¥å›æ»šåˆ°ç›®æ ‡é…ç½®ï¼Œé¿å…åŠ¨æ€å¯¹è±¡ç´¢å¼•
      this.config = { ...targetConfig.config };
    }
    this.recordConfigChange(`Rollback ${steps} steps`);

    return true;
  }
}

