import React from 'react';

export interface ReactScanConfig {
  enabled: boolean;
  showToolbar: boolean;
  log: boolean;
  trackUnnecessaryRenders: boolean;
  animationSpeed: 'slow' | 'fast' | 'off';
}

/**
 * æ£€æŸ¥æ˜¯å¦åº”è¯¥å¯ç”¨ React Scan
 *
 * å¯ç”¨æ¡ä»¶ï¼š
 * 1. å¿…é¡»æ˜¯å¼€å‘ç¯å¢ƒ (NODE_ENV === 'development')
 * 2. ç”Ÿäº§ç¯å¢ƒå¼ºåˆ¶ç¦ç”¨
 * 3. å¼€å‘ç¯å¢ƒé»˜è®¤å¯ç”¨ï¼Œå¯é€šè¿‡ç¯å¢ƒå˜é‡ç¦ç”¨
 */
export const shouldEnableReactScan = (): boolean => {
  // ç”Ÿäº§ç¯å¢ƒå¼ºåˆ¶ç¦ç”¨
  if (process.env.NODE_ENV === 'production') {
    return false;
  }

  // éå¼€å‘ç¯å¢ƒç¦ç”¨
  if (process.env.NODE_ENV !== 'development') {
    return false;
  }

  // å¼€å‘ç¯å¢ƒï¼šæ£€æŸ¥æ˜¯å¦æ˜ç¡®ç¦ç”¨
  const explicitlyDisabled =
    process.env.NEXT_PUBLIC_DISABLE_REACT_SCAN === 'true';

  // å¼€å‘ç¯å¢ƒé»˜è®¤å¯ç”¨ï¼Œé™¤éæ˜ç¡®ç¦ç”¨
  return !explicitlyDisabled;
};

/**
 * é»˜è®¤ React Scan é…ç½®
 */
export const DEFAULT_REACT_SCAN_CONFIG: ReactScanConfig = {
  enabled: shouldEnableReactScan(),
  showToolbar: true,
  log: false, // é¿å…æ§åˆ¶å°å™ªéŸ³
  trackUnnecessaryRenders: true, // æ£€æµ‹ä¸å¿…è¦çš„æ¸²æŸ“
  animationSpeed: 'fast',
};

/**
 * åˆå§‹åŒ– React Scan
 *
 * è‡ªåŠ¨è·Ÿéšå¼€å‘ç¯å¢ƒå¯ç”¨/ç¦ç”¨ï¼Œç”Ÿäº§ç¯å¢ƒå¼ºåˆ¶ç¦ç”¨
 */
export const initReactScan = async (config: Partial<ReactScanConfig> = {}) => {
  // ä½¿ç”¨ç»Ÿä¸€çš„å¯ç”¨æ£€æŸ¥é€»è¾‘
  const shouldEnable = shouldEnableReactScan();

  if (!shouldEnable) {
    // åœ¨å¼€å‘ç¯å¢ƒæ˜¾ç¤ºç¦ç”¨ä¿¡æ¯
    if (process.env.NODE_ENV === 'development') {
      console.log(
        'ğŸ” React Scan disabled (set NEXT_PUBLIC_DISABLE_REACT_SCAN=false to enable)',
      );
    }
    return;
  }

  try {
    // åŠ¨æ€å¯¼å…¥ React Scanï¼Œé¿å…å½±å“ç”Ÿäº§æ„å»º
    const { scan } = await import('react-scan');

    const finalConfig = {
      ...DEFAULT_REACT_SCAN_CONFIG,
      ...config,
    };

    scan(finalConfig);

    // å¼€å‘ç¯å¢ƒæ—¥å¿—
    console.log('ğŸ” React Scan initialized with config:', finalConfig);
    console.log('ğŸ’¡ To disable: set NEXT_PUBLIC_DISABLE_REACT_SCAN=true');
  } catch (error) {
    console.warn('Failed to initialize React Scan:', error);
  }
};

/**
 * React Scan åŒ…è£…å™¨ç»„ä»¶
 *
 * ç”¨äºåœ¨ React ç»„ä»¶ä¸­åˆå§‹åŒ– React Scan
 */
export const ReactScanWrapper = ({
  children,
}: {
  children: React.ReactNode;
}) => {
  if (typeof window !== 'undefined') {
    // å®¢æˆ·ç«¯åˆå§‹åŒ–
    initReactScan();
  }

  return children;
};

import {
  ANIMATION_DURATION_VERY_SLOW,
  COUNT_FIVE,
  COUNT_PAIR,
  COUNT_TEN,
  ONE,
  PERCENTAGE_FULL,
  SECONDS_PER_MINUTE,
  ZERO,
} from '@/constants';
import { MAGIC_20 } from '@/constants/count';
import { MINUTE_MS } from '@/constants/time';

/**
 * é€Ÿç‡é™åˆ¶å·¥å…·
 * Rate limiting utilities
 */

/**
 * Rate limiting constants
 */
const RATE_LIMIT_CONSTANTS = {
  DEFAULT_MAX_REQUESTS: COUNT_TEN,
  DEFAULT_WINDOW_MS: MINUTE_MS, // 1 minute
  CLEANUP_INTERVAL_MINUTES: COUNT_FIVE,
  MINUTES_TO_MS: SECONDS_PER_MINUTE,
  SECONDS_TO_MS: ANIMATION_DURATION_VERY_SLOW,
} as const;

/**
 * Rate limit entry interface
 */
interface RateLimitEntry {
  count: number;
  resetTime: number;
}

/**
 * Rate limit store
 */
const rateLimitStore = new Map<string, RateLimitEntry>();

/**
 * Rate limiting utility
 */
export function rateLimit(
  identifier: string,
  maxRequests: number = RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS,
  windowMs: number = RATE_LIMIT_CONSTANTS.DEFAULT_WINDOW_MS,
): boolean {
  const now = Date.now();
  const entry = rateLimitStore.get(identifier);

  if (!entry || now > entry.resetTime) {
    // First request or window expired
    rateLimitStore.set(identifier, {
      count: ONE,
      resetTime: now + windowMs,
    });
    return true;
  }

  if (entry.count >= maxRequests) {
    // Rate limit exceeded
    return false;
  }

  // Increment count
  entry.count += ONE;
  rateLimitStore.set(identifier, entry);
  return true;
}

/**
 * Get rate limit status for an identifier
 */
export function getRateLimitStatus(identifier: string): {
  remaining: number;
  resetTime: number;
  isLimited: boolean;
} {
  const now = Date.now();
  const entry = rateLimitStore.get(identifier);

  if (!entry || now > entry.resetTime) {
    return {
      remaining: RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS - ONE,
      resetTime: now + RATE_LIMIT_CONSTANTS.DEFAULT_WINDOW_MS,
      isLimited: false,
    };
  }

  const remaining = Math.max(
    ZERO,
    RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS - entry.count,
  );
  return {
    remaining,
    resetTime: entry.resetTime,
    isLimited: remaining === ZERO,
  };
}

/**
 * Clean up expired rate limit entries
 */
export function cleanupRateLimit(): void {
  const now = Date.now();
  for (const [key, entry] of rateLimitStore.entries()) {
    if (now > entry.resetTime) {
      rateLimitStore.delete(key);
    }
  }
}

/**
 * Reset rate limit for a specific identifier
 */
export function resetRateLimit(identifier: string): void {
  rateLimitStore.delete(identifier);
}

/**
 * Get all active rate limit entries (for debugging)
 */
export function getActiveLimits(): Array<{
  identifier: string;
  count: number;
  resetTime: number;
  remaining: number;
}> {
  const now = Date.now();
  const active: Array<{
    identifier: string;
    count: number;
    resetTime: number;
    remaining: number;
  }> = [];

  for (const [identifier, entry] of rateLimitStore.entries()) {
    if (now <= entry.resetTime) {
      active.push({
        identifier,
        count: entry.count,
        resetTime: entry.resetTime,
        remaining: Math.max(
          ZERO,
          RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS - entry.count,
        ),
      });
    }
  }

  return active;
}

/**
 * Advanced rate limiting with different tiers
 */
export interface RateLimitTier {
  name: string;
  maxRequests: number;
  windowMs: number;
}

type DefaultTierKey = 'strict' | 'normal' | 'relaxed' | 'premium';

const defaultTiers: Record<DefaultTierKey, RateLimitTier> = {
  strict: {
    name: 'strict',
    maxRequests: COUNT_FIVE,
    windowMs: MINUTE_MS, // 1 minute
  },
  normal: {
    name: 'normal',
    maxRequests: COUNT_TEN,
    windowMs: MINUTE_MS, // 1 minute
  },
  relaxed: {
    name: 'relaxed',
    maxRequests: MAGIC_20,
    windowMs: MINUTE_MS, // 1 minute
  },
  premium: {
    name: 'premium',
    maxRequests: PERCENTAGE_FULL,
    windowMs: MINUTE_MS, // 1 minute
  },
} as const;

const allowedTierKeys: ReadonlyArray<DefaultTierKey> = [
  'strict',
  'normal',
  'relaxed',
  'premium',
];

const isDefaultTierKey = (value: string): value is DefaultTierKey =>
  allowedTierKeys.includes(value as DefaultTierKey);

/**
 * Rate limit with tier support
 */
export function rateLimitWithTier(
  identifier: string,
  tierName: string = 'normal',
): boolean {
  const tierKey: DefaultTierKey = isDefaultTierKey(tierName)
    ? tierName
    : 'normal';

  const tier = defaultTiers[tierKey];

  return rateLimit(identifier, tier.maxRequests, tier.windowMs);
}

/**
 * Create custom rate limit tier
 */
export function createRateLimitTier(
  name: string,
  maxRequests: number,
  windowMs: number,
): RateLimitTier {
  return { name, maxRequests, windowMs };
}

/**
 * Sliding window rate limiter
 */
interface SlidingWindowEntry {
  timestamps: number[];
}

const slidingWindowStore = new Map<string, SlidingWindowEntry>();

/**
 * Sliding window rate limiting
 */
export function slidingWindowRateLimit(
  identifier: string,
  maxRequests: number = RATE_LIMIT_CONSTANTS.DEFAULT_MAX_REQUESTS,
  windowMs: number = RATE_LIMIT_CONSTANTS.DEFAULT_WINDOW_MS,
): boolean {
  const now = Date.now();
  const windowStart = now - windowMs;

  let entry = slidingWindowStore.get(identifier);
  if (!entry) {
    entry = { timestamps: [] };
    slidingWindowStore.set(identifier, entry);
  }

  // Remove old timestamps outside the window
  entry.timestamps = entry.timestamps.filter(
    (timestamp) => timestamp > windowStart,
  );

  // Check if we're within the limit
  if (entry.timestamps.length >= maxRequests) {
    return false;
  }

  // Add current timestamp
  entry.timestamps.push(now);
  return true;
}

/**
 * Clean up sliding window entries
 */
export function cleanupSlidingWindow(): void {
  const now = Date.now();
  const maxAge = RATE_LIMIT_CONSTANTS.DEFAULT_WINDOW_MS * COUNT_PAIR; // Keep entries for 2x window size

  for (const [key, entry] of slidingWindowStore.entries()) {
    const oldestTimestamp = Math.min(...entry.timestamps);
    if (now - oldestTimestamp > maxAge) {
      slidingWindowStore.delete(key);
    }
  }
}

// Clean up rate limit entries every 5 minutes
if (typeof setInterval !== 'undefined') {
  setInterval(
    () => {
      cleanupRateLimit();
      cleanupSlidingWindow();
    },
    RATE_LIMIT_CONSTANTS.CLEANUP_INTERVAL_MINUTES *
      RATE_LIMIT_CONSTANTS.MINUTES_TO_MS *
      RATE_LIMIT_CONSTANTS.SECONDS_TO_MS,
  );
}

