  describe('Fallback and Degradation Strategies', () => {
    it('should use fallback endpoint when primary fails', async () => {
      // Primary endpoint fails
      let callCount = 0;
      global.fetch = vi.fn().mockImplementation((url) => {
        callCount++;
        if (url.includes('/primary')) {
